mapper.select:
select userid
from vote_tb
where CHOICENUM = #{CHOICENUM}

select CHOICENUM, IMAGETYPE, IMAGENAME
from choice_tb
where CHOICENUM = #{CHOICENUM}

select CHOICENUM, POSTNUM, CONTENT
from choice_tb
where postnum = #{postId}

select userid
from vote_tb
where CHOICENUM = #{choiceId}

select votenum
from vote_tb
where postnum= #{postId} and userid =#{userId}

select postnum
from vote_tb
where userid = #{userid}
