mapper.select:
select id
from user_tb
WHERE id =#{id}

mapper.update:
UPDATE user_tb
SET id =#{id} , pw =#{password} , name =#{name} , birth =#{ birth},
phonenumber =#{tel} , interest =#{interest} , email=#{email},
gender=#{gender}, grade=#{grade}, semo=#{semo}
WHERE id = #{id}

mapper.select:
select fnum
from friends_tb
where userid = ${user} and friendid = ${friend}

mapper.delete:
delete
from friends_tb
where userid = ${user} and friendid = ${friend}
